Action.script:
local status = "";
if (isActive("First AID (automatico)", "trigger") > 0) then
  disableTrigger("First AID (automatico)")
  status = "<red>OFF<reset>"
  setButtonStyleSheet("First Aid", [[
  QWidget {
    background-color: red;
    border: 3px #777777;
  } ]])
else
  enableTrigger("First AID (automatico)")
  status = "<green>ON<reset>"
  setButtonStyleSheet("First Aid", [[
  QWidget {
    background-color: green;
    border: 3px #777777;
  } ]])
end
cecho("Trigger First AID (automatico) "..status.."\n")

